// Function to transform metrics data to HistogramChartFormat
function transformToHistogramChartFormat(metricasPorAplicacion) {
  const results = {};

  for (let i = 0; i < metricasPorAplicacion.length; i++) {
    const category = metricasPorAplicacion[i].dimensionMap["Apdex category"];
    const timestamps = metricasPorAplicacion[i].timestamps;
    const values = metricasPorAplicacion[i].values;

    for (let j = 0; j < timestamps.length; j++) {
